import{_ as o,Q as s,S as a,U as e,a8 as t,W as c,H as _}from"./framework-d7e1aa10.js";const l={},r=e("h1",{id:"服务网格",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#服务网格","aria-hidden":"true"},"#"),t(" 服务网格")],-1),i={class:"hint-container info"},h=e("p",{class:"hint-container-title"},"相关文章",-1),d={href:"https://zhuanlan.zhihu.com/p/413044096#:~:text=%E6%9C%8D%E5%8A%A1%E7%BD%91%E6%A0%BC%E6%9C%AC%E8%B4%A8%E4%B8%8A%E6%98%AF,%E5%8A%A1%E5%BC%80%E5%8F%91%E4%B8%8E%E5%88%9B%E6%96%B0%E4%B8%8A%E3%80%82",target:"_blank",rel:"noopener noreferrer"},E=e("blockquote",null,[e("p",null,"TODO")],-1),p=e("p",null,"与其单独为每一个微服务添加众多的逻辑，更加合理的做法是将这些与实际业务逻辑无关的逻辑从微服务中分离出来，放入到它的一个代理程序中，集中处理。这个小程序是个第三方程序，我们可以轻松配置应用程序，而无需担心逻辑是如何实现的。",-1),u=e("p",null,"此外开发人员也不必将代理配置添加到微服务的部署文件中，因为服务网格有一个控制平面，它会自动在每个微服务中添加这个代理。所以现在微服务可以通过这些代理相互通信，以实现由控制平面组成的服务到服务通信，而这些代理就是服务网格。因此现在开发人员就可以专注于开发实际的业务逻辑。",-1),B=e("p",null,"服务网格本质上是一张负责微服务之间通信的网络，它将服务治理能力与业务开发解耦，使开发人员更专注在业务开发与创新上。同时将服务治理能力下沉到基础设施，把专业的事情交给专业人去做，治理能力的迭代更新也可以不依赖于业务。在如今微服务架构架构逐渐走向主流的时代，服务网格正为我们更好地实现云原生保驾护航。",-1);function f(A,m){const n=_("ExternalLinkIcon");return s(),a("div",null,[r,e("div",i,[h,e("p",null,[e("a",d,[t("服务网格是什么“格”？"),c(n)])])]),E,p,u,B])}const k=o(l,[["render",f],["__file","服务网格.html.vue"]]);export{k as default};
